import React , {useState ,useContext , useEffect} from 'react'
import { Context } from '../context'
import { Link } from 'react-router-dom'
import useDelayedState from 'use-delayed-state'
import  YouTube from 'react-youtube'
import '../style/topRatedSlide.css'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function TopRatedSlideProva(){
    const {wishMovie , addToCart , removeToCart , CartMovie , addTowishMovie , removeTowishMovie , topRatedMovie} = useContext(Context)
    const [hover , setHover] = useDelayedState(false)
    const [hoverVideo ,setHoverVideo] = useDelayedState(false)
    const [ getVideo , setGetVideo] = useState('')
    useEffect(()=>{
        fetch(`https://api.themoviedb.org/3/${topRatedMovie.movie}/${topRatedMovie.id}/videos?api_key=efc42faadb8a20858df7818520bb9d80&language=en-US&name&append_to_response=videos`)
        .then(response => response.json())
        .then(data=> setGetVideo(data.results[0]))
      },[topRatedMovie.id])
    function hoverTrue(){
        setHover(true , 300 ) 
        setHoverVideo(true,1000 )  
        // topRatedMovie.hoverTrue()      
      }
      function hoverFalse(){
         setHover(false , -300 )
         setHoverVideo(false ,-1000 )
        //  topRatedMovie.hoverFalse()
        }
    function cart(){
        const isAdded = CartMovie.find(movie => movie.id === topRatedMovie.id)
        if(isAdded){
            return (<div className='list-shopping'>
                   <i className="ri-shopping-bag-fill topRated-unrent-icon"
                     onClick={()=>removeToCart(topRatedMovie.itemCart.id)} >
                    </i>
                   </div>)
        }else {
            return ( <div className='list-shopping'> 
                   <i className="ri-shopping-bag-line topRated-unrent-icon"
                    onClick={()=>addToCart(topRatedMovie.itemCart)}>
                    </i>
          </div>)
        }
    }
    function heart(){
        const isAdded = wishMovie.find(movie => movie.id === topRatedMovie.id)
        if(isAdded){
            return (<div className='top-shopping'>
                    <i className="ri-heart-fill heart-topRated" 
                     onClick={()=>removeTowishMovie(topRatedMovie.itemCart.id)}>
                     </i>
                     </div>
                     )
                           }else {
                            return (<div className='top-shopping'>
                            <i className="ri-heart-line heart-topRated" 
                             onClick={()=>addTowishMovie(topRatedMovie.itemCart)}>
                             </i>
                            </div>
                             )
                           }
                        }
                        // adult
                        // : 
