                     : <img src={`https://image.tmdb.org/t/p/original/${topRatedMovie[1].backdrop_path}`} alt={topRatedMovie[1].original_title}  />}
                 <div className='list-padding-ctn-topRated' >
                  <div className='list-title-flex-topRated'>
                   <h1 className='list-title-topRated'>{topRatedMovie[1].original_title}</h1>
                  <div className='list-icon-ctn-topRated'>
                     {cart()}
                     {heart()}
                   <div className='list-shopping'>
                    <Link to={`/${topRatedMovie[1].movie}/${topRatedMovie[1].id}`} state={'Home'}>
                      <i className="ri-spy-line spy-topRated "></i>
                    </Link>
                   </div>
                  </div>
                 </div>                                 
                 </div>
                 </div>
             </div> : 
                <div className={hover? 'img-topRated-ctn margin-video' :'img-topRated-ctn'} >
                <img src={`https://image.tmdb.org/t/p/original/${topRatedMovie[1].poster_path}`} alt={topRatedMovie[1].original_title}   />
               </div>
               }
            
