import React  from 'react'
import {  Navigate ,Outlet , useLocation , redirect} from 'react-router-dom'


export default function AuthRequired(){
  const location = useLocation()
  console.log(location)
  let json = localStorage.getItem('login')
    if(!json){
      return  <Navigate to='/login'  state={{message : 'You must log in first!'}} replace/>
    }
    return redirect(location.pathname)
}
