import React , {useState} from 'react'
import { Link } from 'react-router-dom'
import '../style/header.css'

export default function Login(){
   
    const [createAccount , setCreateAccount] = useState(false)
    const [log ,setLog] = useState({email:'',password:'',name:''})
    const [register ,setRegister] = useState([])
    function handleSubmit(e){
      const { value, name} = e.target
      setLog(user=>{ return {...user,[name]: value }})
    }
    function handleRegister(){
       setRegister(log)
    }
    console.log(register)
    function loginCreateAccount(){
    if(createAccount){
         return (
            <div className='login-ctn'>
            <div className='title-ctn'> 
             <Link to='/'><h1 className='title-login'  >My Movies<span className='span-title'>rent</span></h1></Link>
            <div className='input-login-ctn'>
                <p>email: bob@gmail.com ,  pasw: 1234</p>
                <p>is active</p>
                <form className='form-login' onChange={handleSubmit}>
                 <input type='email' name='email' placeholder='Email'/>
                 <input type='password' name='password' placeholder='Password'/>
                 {createAccount && <input type='text' name='name' placeholder='Name' />}
                 <button onClick={handleRegister}>{createAccount ? 'Register' : 'Log In'}</button>
                </form>
                <p>Do you have a account ? 
                <span style={{color:'#cf4d4d',fontWeight:'600', cursor:'pointer'}} onClick={()=> setCreateAccount(!createAccount)}>Log in</span>
            </p>     
            </div>
            </div>
            <img src='/images/loginMyRent.jpg' alt='Login Pages' />
        </div>
         )
    }else{
        return(
            <div className='login-ctn'>
            <div className='title-ctn'> 
             <Link to='/'><h1 className='title-login'  >My Movies<span className='span-title'>rent</span></h1></Link>
            <div className='input-login-ctn'>
                <p>email: bob@gmail.com ,  pasw: 1234</p>
                <p>is active</p>
                <form className='form-login' onChange={handleSubmit}>
                 <input type='email' name='email' placeholder='Email'/>
                 <input type='password' name='password' placeholder='Password'/>
                 {createAccount && <input type='text' name='name' placeholder='Name' />}
                 <button onClick={handleRegister}>{createAccount ? 'Register' : 'Log In'}</button>
                </form>
                <p>Don't have a account ? 
                <span style={{color:'#cf4d4d',fontWeight:'600', cursor:'pointer'}} onClick={()=> setCreateAccount(!createAccount)}> Create One</span>
            </p>     
            </div>
            </div>
            <img src='/images/loginMyRent.jpg' alt='Login Pages' />
        </div>
        )
    }
    }
    return(
       {loginCreateAccount}
    )
}
