import React , {useState ,useContext , useEffect} from 'react'
import { Link } from 'react-router-dom';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import '../style/TopRated.css'
import '../style/slideStyle.css'
import Movie from '../componentChild/slideMovies';
import { Context } from '../context';
import Categories from '../componentChild/categories';
import useDelayedState from 'use-delayed-state'
import  YouTube from 'react-youtube'
// import TopRatedSlide from '../componentChild/topRatedSlide';
import '../style/header.css'

export default function SlideSecondarySeries(){

    const { categoriesArray } = React.useContext(Context)

            //  IMPOSTAZIONI SLIDE 
    function CustomPrevArrowMovie(props) {
      const { onClick } = props;
      return (
        <button className="prev-top" onClick={onClick}>
          <i className="ri-arrow-left-s-line"></i>
        </button>
      );
    }    
    function CustomNextArrowMovie(props) {
      const { onClick } = props;
      return (
        <button className="next-top" style={{heigth:'100px'}} onClick={onClick}>
          <i className="ri-arrow-right-s-line"></i>
        </button>
      );
    }
    function CustomNextArrowTopRated(props) {
      const { onClick } = props;
      return (
        <button className="next-topRated" style={{heigth:'100px'}} onClick={onClick} >
          <i className="ri-arrow-right-s-line"></i>
        </button>
      );
    }
    function CustomPrevArrowTopRated(props) {
      const { onClick } = props;
      return (
        <button className="prev-topRated" onClick={onClick}>
          <i className="ri-arrow-left-s-line"></i>
        </button>
      );
    }  
  
    const settingsTopRated = {      
      pauseOnHover: true,
       dots: false,
       infinite: true,
       speed: 500,
       slidesToShow: 5,
       slidesToScroll: 2,
       autoplay: true,
       autoplaySpeed: 6500,
       prevArrow: <CustomPrevArrowTopRated  />,
       nextArrow: <CustomNextArrowTopRated  />
     }
  const settingSlide = {
      dots: false,
      infinite: true,
      speed: 2000,
      slidesToShow: 5,
      slidesToScroll: 5,
      autoplay: false,
      autoplaySpeed: 6000,
      heigth:400,
      prevArrow: <CustomPrevArrowMovie />,
      nextArrow: <CustomNextArrowMovie />
    }  
    const TopRatedSlide = (props)=> {
        
      const {wishMovie , addToCart , removeToCart , CartMovie , addTowishMovie , removeTowishMovie} = useContext(Context)
      const [hover , setHover] = useDelayedState(false)
      const [hoverVideo ,setHoverVideo] = useDelayedState(false)
      const [ getVideo , setGetVideo] = useState('')
      const isLeftSelected = selectedSlide === props.movies[index - 1]?.id;
      const imageClass = isLeftSelected ? 'topRated-slide-image left-selected' : 'topRated-slide-image';
      useEffect(()=>{
          fetch(`https://api.themoviedb.org/3/${props.movie}/${props.id}/videos?api_key=efc42faadb8a20858df7818520bb9d80&language=en-US&name&append_to_response=videos`)
          .then(response => response.json())
          .then(data=> setGetVideo(data.results[0]))
        },[props.id])
      function hoverTrue(){
          setHover(true , 300 ) 
          setHoverVideo(true,1000 )  
          props.hoverTrue()      
        }
        function hoverFalse(){
           setHover(false , -300 )
           setHoverVideo(false ,-1000 )
           props.hoverFalse()
          }
      function cart(){
          const isAdded = CartMovie.find(movie => movie.id === props.id)
          if(isAdded){
              return (<div className='list-shopping'>
                     <i className="ri-shopping-bag-fill topRated-unrent-icon"
                       onClick={()=>removeToCart(props.itemCart.id)} >
                      </i>
                     </div>)
          }else {
              return ( <div className='list-shopping'> 
                     <i className="ri-shopping-bag-line topRated-unrent-icon"
                      onClick={()=>addToCart(props.itemCart)}>
                      </i>
            </div>)
          }
      }
      function heart(){
          const isAdded = wishMovie.find(movie => movie.id === props.id)
          if(isAdded){
              return (<div className='top-shopping'>
                      <i className="ri-heart-fill heart-topRated" 
                       onClick={()=>removeTowishMovie(props.itemCart.id)}>
                       </i>
                       </div>
                       )
                             }else {
                              return (<div className='top-shopping'>
                              <i className="ri-heart-line heart-topRated" 
                               onClick={()=>addTowishMovie(props.itemCart)}>
                               </i>
                              </div>
                               )
                             }
                          }
                          
      return(
          <>
          <div className='topRated-slide-big' onMouseEnter={()=>hoverTrue()}  onMouseLeave={()=>hoverFalse()}>           
             {hover ? <div className='topRated-list-ctn'  onMouseLeave={()=>hoverFalse()}>        
                   <div className='list-overview-topRated' onMouseLeave={()=>hoverFalse()}> 
                     {hoverVideo ? <YouTube className="youtube-trailer-topRated"  videoId={`${getVideo.key}`} autoPlay={true} ></YouTube> 
                       : <img src={props.img} alt={props.title} className={imageClass} />}
                   <div className='list-padding-ctn-topRated' >
                    <div className='list-title-flex-topRated'>
                     <h1 className='list-title-topRated'>{props.title}</h1>
                    <div className='list-icon-ctn-topRated'>
                       {cart()}
                       {heart()}
                     <div className='list-shopping'>
                      <Link to={`/${props.movie}/${props.id}`} state={props.state}>
                        <i className="ri-spy-line spy-topRated "></i>
                      </Link>
                     </div>
                    </div>
                   </div>                                 
                   </div>
                   </div>
               </div> : 
                  <div className={hover? 'img-topRated-ctn hover-margin' :'img-topRated-ctn'} >
                  <img src={props.url} alt={props.title} className={imageClass}  />
                 </div>
                 }
              
          </div>
          
          </>
      )
    }
            // MAPPATURA ARRAY MOVIE 
    const categoriesMap = categoriesArray.map((movie,index) =>{
    return (<Categories key={index} id={movie.id} value={movie.value} name={movie.name}/>)})
    
     const categoriesSeries = categoriesArray.map((movie,index) => {
      return (
        <div key={index} className={`slide-movie-ctn-${movie.value}`}>
        <div className='title-ctn-slide'>
        <h1 className='title-slide-red'>My Movies<span className='span-slide'>rent</span></h1>
        <h1 className='toprated-title'>{movie.name}</h1>
        <Link to={`/${movie.id}`} className='toprated-link' >See more </Link>
        <Link to={`/${movie.id}`} className='toprated-link' ><i className="ri-arrow-right-s-line link-icon" ></i></Link>        
        </div>
          <Slider {...settingSlide}  className='slider-class-movie' >
            {movie.series.map((movie,index)=>{
              return(
                <Movie
                movie={movie.movie}
                key={index}  
                ids={movie.ids}
                id={movie.id}
                url={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
                isFavorite={movie.isFavorite}
                title={movie.title}
                date={movie.release_date}
                vote={movie.vote_average}
                overview={ movie.overview }
                genres={movie.genre_ids}
                state={{search : null ,type:null ,name : null , title: 'Home'}}
                 itemCart={{id : movie.id , isFavorite : movie.isFavorite , img : `https://image.tmdb.org/t/p/original/${movie.poster_path}`,
                            title : movie.title ,overview : movie.overview , vote : movie.vote_average , date : movie.release_date,
                            price:2.99 ,quantity : 1 , movie:movie.movie }} />
              )
            })}
          </Slider>
        </div> 
      )
    })
  
    const topRatedSlideMovie = categoriesArray.map((movie,index) => {
      return (
        <div key={index} className={`slide-movie-ctn-${movie.value}-topRated`} >
        <div className='title-ctn-slide'>
        <h1 className='title-slide-red'>My Movies<span className='span-slide'>rent</span></h1>
        <h1 className='toprated-title'>{movie.name}</h1>
        <Link to={`/${movie.id}`} className='toprated-link' >See more </Link>
        <Link to={`/${movie.id}`} className='toprated-link' ><i className="ri-arrow-right-s-line link-icon" ></i></Link>        
        </div>
          <Slider {...settingsTopRated}   className='slider-class-movie-topRated-big' >
            {movie.series.map((movie,index)=>{
              return(
                <TopRatedSlide
                movie={movie.movie}
                key={index}
                ids={movie.ids}
                id={movie.id}
                url={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
                  img={`https://image.tmdb.org/t/p/original/${movie.backdrop_path}`}
                isFavorite={movie.isFavorite}
                title={movie.title}
                date={movie.release_date}
                vote={movie.vote_average}
                overview={ movie.overview }
                genres={movie.genre_ids}
                state={{search : null ,type:null ,name : null , title: 'Home'}}
                 itemCart={{id : movie.id , isFavorite : movie.isFavorite , img : `https://image.tmdb.org/t/p/original/${movie.poster_path}`,
                            title : movie.title ,overview : movie.overview , vote : movie.vote_average , date : movie.release_date,
                            price:2.99 ,quantity : 1 , movie:movie.movie }} />
              )
            })}
          </Slider>
        </div> 
      )
    })
    
    const topRatedSlideMovieFirst = topRatedSlideMovie[0]            

   const FirstSlideSeries = categoriesSeries.slice(1,3)
   const SecondSlideSeries = categoriesSeries.slice(3 ,12)
     return(
     <>
        {FirstSlideSeries }
        {topRatedSlideMovieFirst}
         <div className='slide-movie-ctn-categories'>
        <div className='title-ctn-slide'>
        <h1 className='title-slide-red'>My Movies<span className='span-slide'>rent</span></h1>
        <h1 className='toprated-title'>Categories</h1>                
        </div>
          <Slider {...settingSlide} className='slider-class-movie' >
           {categoriesMap} 
          </Slider>
        </div> 
        {SecondSlideSeries }
        </>
        
     )
         
}
