import React , {useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../style/header.css'

export default function Login(){
    const [createAccount , setCreateAccount] = useState(false)
    const [loginData , setLoginData] = useState({email :'bob@gmail.com' , password:'1234' , account:'Fabio'})
    const navigate = useNavigate()
    
    function handleData(event){
      const {name , value} = event.target
      setLoginData(data => {
        return {...data , [name] : value }
      })
     }
     console.log(loginData)
    //  function submitLoginData(){

    //  }
    return(
        <div className='login-ctn'>
            <div className='title-ctn'> 
             <h1 className='title-login'  >My Movies<span className='span-title'>rent</span></h1>
            <div className='input-login-ctn'>
                <p>email: bob@gmail.com ,  pasw: 1234</p>
                <p>is active</p>
                <form className='form-login'>
                 <input onChange={handleData} type='email' name='email' placeholder='Email'/>
                 <input onChange={handleData} type='password' name='password' placeholder='Password'/>
                 {createAccount && <input onChange={handleData} type='text' name='account' placeholder='Name' />}
                 <button>{createAccount ? 'Register' : 'Log In'}</button>
                </form>
                <p>{createAccount ? "You have a account..." : "Don't have a account..."}
                <span style={{color:'#cf4d4d',fontWeight:'600', cursor:'pointer'}} onClick={()=> setCreateAccount(!createAccount)}>{createAccount ? ' Log in' :' Create One'}</span>
            </p>     
            </div>
            </div>
            <img src='/images/loginMyRent.jpg' alt='Login Pages' />
        </div>
    )
}
