import {useState ,useEffect, useRef} from 'react'

function useHover(){
    const [hover , setHover] = useState(false)
    const ref = useRef(null)

    function Enter(){
        setHover(true)
    }
    function Leave(){
        setHover(false)
    }
    function MobileHover(){
        setHover(!hover)
    }
    useEffect(()=>{
        const refcurrent = ref.current
        refcurrent.addEventListener('mouseenter' ,Enter )
        refcurrent.addEventListener('mouseleave' , Leave)
        refcurrent.addEventListener('click' , MobileHover)
        

        return () => {
            refcurrent.removeEventListener('mouseenter' ,Enter )
        refcurrent.removeEventListener('mouseleave' , Leave)
        refcurrent.removeEventListener('click' , MobileHover)
      
        }
    },[ref])
    return [hover , ref]
}

export default useHover
